sys_variable_value.value:
(function execute(inputs, outputs) {

if (inputs.respType === 'service_name'){
    outputs.clean_output = inputs.dataIn
    .replace(/##/g, '');
}
else{
    outputs.clean_output = inputs.dataIn
    .replace(/##.*$/gm, '')
    .replace(/^\s\n+|\s\n+$/g,'');  
}

if(inputs.respType === 'Now LLM'){
    outputs.clean_output = inputs.dataIn
    .replace(/Response : {"model_output":"/g, '')
    .replace(/"}/g, '')
    .replace(/\\n/g, '')
    .replace(/\*\*/g, '');
}
})(inputs, outputs);

