sys_variable_value.value:
(function execute(inputs, outputs) {

var roles = new GlideRecord('sys_user_role');
var userRole = new GlideRecord('sys_user_has_role');
var roleSysIDs;

// Get all of the roles that we are being asked to grant to the user
roles.addQuery('name','IN', inputs.list.toString());
roles.query();

//gs.info("Found Roles from Input List: " + roles.getRowCount());


// Loop through the roles to see if the user already has them
while(roles.next()){

    //gs.info("Role is: " + roles.sys_id);
    //gs.info("User is: " + inputs.user.sys_id);

    // Query for the user role
    userRole.initialize();
    userRole.addQuery('user',inputs.user.sys_id);
    userRole.addQuery('role',roles.sys_id);
    userRole.query();
    
    // If the user doesn't have the role give it to them
    if(userRole.getRowCount() == 0){
    //    gs.info("Inserting " + roles.name);
        userRole.initialize();
        userRole.user = inputs.user.sys_id;
        userRole.role = roles.sys_id;
        userRole.insert();
    }
    
    //if(typeof roleSysIDs !== 'undefined'){
    //    roleSysIDs=roleSysIDs + ',' + roles.sys_id;   
    //}else{
    //    roleSysIDs = roles.sys_id;
    //}
}

 //outputs.arrayout = roleSysIDs.split(',');

})(inputs, outputs);

