sys_script_include.script:
var GeneratePlaybookCallbackHandler = Class.create();
GeneratePlaybookCallbackHandler.prototype = {
    initialize: function() {},

    generationUpdated: function(state, requestId, meta, playbookJSON) {
        var logInfo = {
            state,
            requestId,
            meta,
            playbookJSON,
            playbookId: JSON.parse(playbookJSON)?.process?.sysId?.value
        };
        gs.info("GeneratePlaybookCallbackHandler | generationUpdated | " + JSON.stringify(logInfo, null, 4));

        switch (state) {
            case 'NEW':
                break;
            case 'IN_PROGRESS':
                break;
            case 'COMPLETED':
                break;
            case 'PERSISTED': {
                // Playbook has been saved
                const playbookId = JSON.parse(playbookJSON)?.process?.sysId?.value;
                const table_name = JSON.parse(meta)?.table_name;
                const service = JSON.parse(meta)?.service_name;
                const servid_id = JSON.parse(meta)?.service_id;

                gs.info("GeneratePlaybookCallbackHandler | Playbook ID: " + playbookId + " Service: " + service + " Service ID: " + servid_id + " Table: " + table_name);

                var ti = new GlideRecord('sys_pd_trigger_instance');
                ti.process_definition = playbookId;
                ti.trigger_type = 'ab6951170f1200108c87f4f0ff767e4f'; //Record Created
                var tiId = ti.insert();

                gs.info("GeneratePlaybookCallbackHandler: Trigger Instance: " + tiId);

                var tiVarTable = new GlideRecord('sys_variable_value');
                tiVarTable.action = 'INSERT_OR_UPDATE';
                tiVarTable.document = 'sys_pd_trigger_instance';
                tiVarTable.document_key = tiId;
                tiVarTable.order = 100;
                tiVarTable.value = table_name;
                tiVarTable.variable = 'a8ae38d1ff2200108c8733af793bf15c'; //This is table variable
                tiVarTable.insert();

                var tiVarCondition = new GlideRecord('sys_variable_value');
                tiVarCondition.action = 'INSERT_OR_UPDATE';
                tiVarCondition.document = 'sys_pd_trigger_instance';
                tiVarCondition.order = 200;
                tiVarCondition.document_key = tiId;
                tiVarCondition.value = 'service=' + servid_id;
                tiVarCondition.variable = '2a8a7851ff2200108c8733af793bf131'; //This is a reference to a condition
                tiVarCondition.insert();

                break;
            }
            case 'CANCELLED':
                break;
        }
    },

    generationError: function(message, requestId, meta) {
        var logInfo = {
            message,
            requestId,
            meta
        };
        gs.info("GeneratePlaybookCallbackHandler | generationError  | " + JSON.stringify(logInfo, null, 4));
    },

    type: 'GeneratePlaybookCallbackHandler'
};
